import React from 'react';
// import { Spinner } from 'react-spinkit';

//components
import Header from '../../components/01_global/header/Header';
import RightFixed from '../../components/01_global/rightFixed/RightFixed';

//images
import weddell_hero from '../../images/weddell/hero.png';

function Weddell() {
  return (
    <div className="weddell">
      <Header
        first="background"
        second="research"
        third="design-process"
        fourth="final-design"
        fifth="learnings"
      />
      <RightFixed />
      <section className="grid-wrapper">
        <div id="introduction">
          <img className="hero gc-060" src={weddell_hero} alt="hero" />
        </div>
        <div id="background"></div>
        <div id="research"></div>
        <div id="design-process"></div>
        <div id="final-design"></div>
        <div id="learnings"></div>
      </section>
    </div>
  );
}

export default Weddell;

