import React from 'react';
import ReactDOM from 'react-dom';

import ProjectHeader from '../../components/01_sections/header/ProjectHeader';

class Weddell extends React.Component {
  state = {
    iFrameHeight: '0px'
  };

  render() {
    return (
      <div>
        <ProjectHeader />
        <iframe
          style={{
            width: '100%',
            height: this.state.iFrameHeight,
            overflow: 'visible'
          }}
          onLoad={() => {
            const obj = ReactDOM.findDOMNode(this);

            console.log(obj);

            this.setState({
              iFrameHeight:
                obj.lastElementChild.contentWindow.document.body.scrollHeight +
                'px'
            });
          }}
          ref="iframe"
          src="http://www.example.com"
          width="100%"
          height={this.state.iFrameHeight}
          scrolling="no"
          frameBorder="0"
          title="iframe"
        ></iframe>
      </div>
    );
  }
}

export default Weddell;
