import styled from 'styled-components';

const Button = styled.div`
  padding: 10px 18px;
  margin: 5px 10px 5px 0px;
  border-radius: 999em;
  border: 1px $blue solid;
  &:hover {
    background: none;
    color: $white;
    background-color: $blue;
  }
  i {
    margin-right: 10px;
  }
`;

export default Button;
