// import { colors } from './colors';

export const lightTheme = {
  //off white
  background: '#F7FAF4',
  //blue
  primary: '#2997FF',
  //gray & black (Text)

  grayhem: 'rgba(0, 0, 0, 0.9)',

  graymem: 'rgba(0, 0, 0, 0.64)',

  graysem: 'rgba(0, 0, 0, 0.38)',

  graydis: 'rgba(0, 0, 0, 0.25)',

  container: '#FFFFFF',
  containerHovered: '#FFFFFF',

  footer: '#E5E9E1',

  shadowDefault: '#eeedea',

  shadowHovered: '#e4e3e0'
};

const darkTheme = {
  colorName: {
    offWhite: '#f7faf4',
    blue: '#0171e3',
    gray: '#888',
    lightGray: '#c4b2b6',
    lightestGray: '#e7eee5',
    black: '#333'
  },

  color: {
    //off white
    background: '#1A1B1F',
    //blue
    primary: '#2997FF',
    //gray & black (Text)

    grayhem: 'rgba(255, 255, 255, 0.95)',

    graymem: 'rgba(255, 255, 255, 0.75)',

    graysem: 'rgba(255, 255, 255, 0.5)',

    graydis: 'rgba(255, 255, 255, 0.3)',

    container: '#2C2F38',
    containerHovered: '#494C56',
    // buttonBG: '#',
    // buttonFG: '#'

    footer: '#101013'
  },

  shadowColor: {
    default: '#252525',
    hovered: '#252525'
  }
};

export default darkTheme;
