import React, { useRef, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { TweenMax, Power2 } from 'gsap';

import resume from '../../../files/resume 01.29.20.pdf';
import AboutDescription from './AboutDescription';

function About(props) {
  let { about, short, description, aboutWord, buttons, learnMore } = useRef(
    null
  );
  // let description = useRef(null);
  // let aboutWord = useRef(null);

  useEffect(() => {
    TweenMax.to(about, 0, {
      css: { visibility: 'visible' }
    });
    TweenMax.from(short, 1, {
      opacity: 0,
      x: -50,
      ease: Power2.easeOut
    });
    TweenMax.from(description, 1.2, {
      opacity: 0,
      x: 50,
      ease: Power2.easeIOut,
      delay: 0.3
    });
    TweenMax.from(aboutWord, 1, {
      opacity: 0,
      ease: Power2.easeOut,
      delay: 0.6
    });
    TweenMax.from(buttons, 1.3, {
      opacity: 0,
      ease: Power2.easeOut,
      delay: 1
    });
    TweenMax.from(learnMore, 1.3, {
      opacity: 0,
      ease: Power2.easeOut,
      delay: 1
    });
    console.log(learnMore);
  });

  return (
    <div
      className="about"
      id="about"
      ref={el => {
        about = el;
      }}
    >
      <div className="container">
        <h3
          className="about-word"
          ref={el => {
            aboutWord = el;
          }}
        >
          About
        </h3>
        <div className="content">
          <h1
            className="short"
            ref={el => {
              short = el;
            }}
          >
            Hi! I am{' '}
            <Link to="AboutMore" className="name">
              Noah Jung{' '}
            </Link>
            — <br /> a Product Designer based in Philadelphia.
          </h1>
          <h3
            className="description"
            ref={el => {
              description = el;
            }}
          >
            <AboutDescription />
          </h3>
          <div
            className="buttons"
            ref={el => {
              buttons = el;
            }}
          >
            <a
              className="linkedin"
              href="https://www.linkedin.com/in/noahsjung/"
              target="blank"
            >
              <i className="fab fa-linkedin"></i>
              LinkedIn
            </a>
            <a className="resume" href={resume} target="blank">
              <i className="far fa-file"></i>
              Resume
            </a>
          </div>
          <div
            className="learn-more"
            ref={el => {
              learnMore = el;
            }}
          >
            <h3>
              <Link to="/AboutMore">Learn more</Link>
              <i className="fas fa-angle-right"></i>
            </h3>
          </div>
        </div>
      </div>
    </div>
  );
}

export default About;
