import { colors } from './colors';

const lightTheme = {
  colorName: {
    offWhite: '#f7faf4',
    blue: '#0171e3',
    gray: '#888',
    lightGray: '#c4b2b6',
    lightestGray: '#e7eee5',
    black: '#333'
  },

  color: {
    background: '${colors.offWhite}',
    primary: '${colors.blue}',
    BigHeader: '#333',
    MedHeader: '#333',
    SmallHeader: '#222',
    SmallTitle: '#222',
    Title: '#222',
    Note: '#888',
    SmallParagraph: '222',
    Paragraph: '#222'
  },

  shadowColor: {
    default: '#eeedea',
    hovered: '#e4e3e0'
  }
};

export default lightTheme;
