import styled from 'styled-components';

export const BigHeader = styled.h1`
  font-family: 'Playfair Display', serif;
  font-size: 2.75rem;
  font-weight: 500;
  line-height: 1.6;
  margin-top: 0px;
  margin-bottom: 0px;
  color: ${props =>
    props.gray ? props.theme.colorName.gray : props.theme.color.bigHeader};
`;

export const MedHeader = styled.h2`
  font-family: 'Playfair Display', serif;
  font-size: 2rem;
  font-weight: 400;
  line-height: 1.6;
  margin-top: 0px;
  margin-bottom: 0px;
  color: ${props => props.theme.color.medHeader};
`;

export const SmallHeader = styled.h3`
  font-family: 'Playfair Display', serif;
  font-size: 1.6rem;
  font-weight: 500;
  line-height: 1.3;
  margin-top: 0px;
  margin-bottom: 0px;
  color: ${props => props.theme.color.smallHeader};
`;

export const SmallTitle = styled.h4`
  font-family: 'Roboto', sans-serif;
  font-size: 1.3rem;
  font-weight: 400;
  line-height: 1.45;
  margin-top: 0px;
  margin-bottom: 0px;
  color: ${props => props.theme.color.smallTitle};
`;

export const Title = styled.h5`
  font-family: 'Roboto', sans-serif;
  font-size: 1.3rem;
  font-weight: 500;
  line-height: 1.45;
  margin-top: 0px;
  margin-bottom: 0px;
  color: ${props => props.theme.color.title};
`;

export const Note = styled.h6`
  font-family: 'Roboto Mono', monospace;
  font-size: 0.95rem;
  font-weight: 400;
  line-height: 1.6;
  margin-top: 0px;
  margin-bottom: 0px;
  color: ${props => props.theme.color.note};
`;

export const SmallParagraph = styled.h6`
  font-family: 'Roboto', sans-serif;
  font-size: 1.1rem;
  font-weight: 400;
  line-height: 1.5;
  margin-top: 0px;
  margin-bottom: 0px;
  color: ${props => props.theme.color.smallParagraph};
`;

export const Paragraph = styled.p`
  font-family: 'Roboto', sans-serif;
  font-size: 1.2rem;
  font-weight: 300;
  line-height: 1.6;
  margin-top: 0px;
  margin-bottom: 0px;
  color: ${props => props.theme.color.paragraph};
`;

// export const LinkToPage = styled.a`
//   text-decoration: none;
//   color: ${props => props.theme.color.link};
//   cursor: pointer;
// `;

export const OrderedList = styled.ol`
  font-family: 'Roboto', sans-serif;
  list-style: none;
  display: block;
  counter-reset: list-numbering 0;
`;

export const List = styled.li`
  font-family: 'Roboto', sans-serif;
  color: ${props => props.theme.color.paragraph};
  text-indent: -15px;
  margin-bottom: 1rem;
  margin-left: 45px;
  counter-increment: list-numbering;
  font-size: 1.15rem;
  font-weight: 300;
  line-height: 1.5;
  ::before {
    font-family: 'Roboto', sans-serif;
    content: counter(list-numbering, decimal-leading-zero) '. ';
    margin-left: -1.65em;
    margin-right: 15px;
  }
`;
