//images
import weddell_hero from '../../images/weddell/hero.png';
import Break from '../../components/01_global/content_template/Break';

function Weddell() {
  useEffect(() => {
    window.scrollTo(0, 0);
  });
  return (
    <div className="weddell">
      <Header
        first="background"
        second="research"
        third="design-process"
        fourth="final-design"
        fifth="learnings"
