          <div className="hero-image-container gc-00-6">
            <img className="hero-image" src={weddell_hero} alt="hero" />
          </div>
        </div>
        <div id="background"></div>
        <div id="research"></div>
        <div id="design-process"></div>
        <div id="final-design"></div>
        <div id="learnings"></div>
      </section>
    </div>
  );
}

export default Weddell;

