import React, { useRef, useEffect } from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { TweenMax, Power2 } from 'gsap';
import { LiquidDistortionText } from 'react-text-fun';

//styles
import {
  BigHeader,
  SmallTitle
} from '../../../styles/02_tools/styled_component/components/Fonts';
import Button from '../../../styles/02_tools/styled_component/components/ButtonStyle';

import resume from '../../../files/resume_01.29.20.pdf';
import AboutDescription from './AboutDescription';

const LinkToPage = styled(Link)`
  text-decoration: none;
  color: ${props => props.theme.color.link};
  cursor: pointer;
  &:hover {
    border-bottom-color: transparent;
    background-image: url("data:image/svg+xml;charset=utf8,%3Csvg id='Squiggle-svg' xmlns='http://www.w3.org/2000/svg' xmlns:xlink='http://www.w3.org/1999/xlink' xmlns:ev='http://www.w3.org/2001/xml-events' viewBox='0 0 20 4'%3E%3Cstyle type='text/css'%3E.st0{animation:shift .5s linear infinite;}@keyframes shift {from {transform:translateX(0);}to {transform:translateX(-20px);}}%3C/style%3E%3Cpath fill='none' stroke='dodgerblue' stroke-width='1.5' class='st0' d='M0,3.5 c 5,0,5,-3,10,-3 s 5,3,10,3 c 5,0,5,-3,10,-3 s 5,3,10,3'/%3E%3C/svg%3E");
    background-position: 0 1.2em;
    background-size: auto 5px;
    background-repeat: repeat-x;
    padding-bottom: 10px;
    text-decoration: none;
  }
`;

function About(props) {
  let { about, short, description, aboutWord, buttons, learnMore } = useRef(
    null
  );

  //animation
  useEffect(() => {
    TweenMax.to(about, 0, {
      css: { visibility: 'visible' }
    });
    TweenMax.from(short, 1, {
      opacity: 0,
      x: -50,
      ease: Power2.easeOut,
      delay: 1
    });
    TweenMax.from(description, 1.2, {
      opacity: 0,
      x: 50,
      ease: Power2.easeIOut,
      delay: 1.3
    });
    TweenMax.from(aboutWord, 1, {
      opacity: 0,
      y: 20,
      ease: Power2.easeOut,
      delay: 1.65
    });
    TweenMax.from(buttons, 1.3, {
      opacity: 0,
      y: 20,
      ease: Power2.easeOut,
      delay: 1.5
    });
    TweenMax.from(learnMore, 1.3, {
      opacity: 0,
      y: 20,
      ease: Power2.easeOut,
      delay: 1.65
    });
  });

  return (
    <div
      className="about"
      id="about"
      ref={el => {
        about = el;
      }}
    >
      <SmallTitle
        className="about-word"
        ref={el => {
          aboutWord = el;
        }}
      >
        <LiquidDistortionText
          id="about-word"
          text="ABOUT"
          fontFamily="Roboto"
          fontSize={24}
          fill="#c4b2b6"
          speed={0.7}
          volatility={0.04}
        />
      </SmallTitle>

      <BigHeader
        className="short"
        ref={el => {
          short = el;
        }}
      >
        Hi! I am{' '}
        <Link to="about-me" className="name">
          Noah Jung
        </Link>
        â€” <br /> a Product Designer based in Philadelphia.
      </BigHeader>
      <SmallTitle
        className="description"
        ref={el => {
          description = el;
        }}
      >
        <AboutDescription />
      </SmallTitle>
      <div
        className="buttons"
        ref={el => {
          buttons = el;
        }}
      >
        <Button
          className="linkedin"
          href="https://www.linkedin.com/in/noahsjung/"
          target="blank"
        >
          <i className="fab fa-linkedin"></i>
          LinkedIn
        </Button>
        <a className="resume" href={resume} target="blank">
          <i className="far fa-file"></i>
          Resume
        </a>
      </div>
      <div
        className="learn-more"
        ref={el => {
          learnMore = el;
        }}
      >
        <SmallTitle>
          <Link to="/about-me">Learn more</Link>
          <i className="fas fa-angle-right"></i>
        </SmallTitle>
      </div>
    </div>
  );
}

export default About;
