import React from 'react';
import ExperienceDescriptionBullet from './ExperienceDescriptionBullet';
import experienceData from '../Data/ExperienceData';

class ExperienceIndi extends React.Component {
  state = {
    experienceData
  };

  render() {
    const bulletDescription = this.state.ExperienceDescriptionBullet.map(
      item => (
        <ExperienceDescriptionBullet
          key={item.id}
          description={item.description}
        />
      )
    );
    return (
      <div className="experience-indi">
        <div className="left">
          <img
            className="logo"
            src={this.props.logo}
            alt={this.props.company + ' logo'}
            height="100"
            width="100"
          />
          <div className="text">
            <h3 className="company">{this.props.company}</h3>
            <h4 className="location"> {this.props.location}</h4>
            <h4 className="date"> {this.props.date}</h4>
          </div>
        </div>
        <div className="right">
          <h3 className="title">{this.props.role}</h3>
          <p className="description">{bulletDescription}</p>
        </div>
        <hr />
      </div>
    );
  }
}

export default ExperienceIndi;
